import csvimport pandas as pduserInfo = pd.read_csv('data\employeedata.csv', encoding="unicode_escape")oldEmailAddress = userInfo['Email'].tolist()new_emails = []phone = userInfo['Phone'].tolist()names = userInfo['Name'].tolist()for email in oldEmailAddress:    new_emails.append(email.replace("@helpinghands.cm", "@handsinhands.org"))print(new_emails)header = ['Name', 'Email', 'Phone']my_dict = {"Name": names, "Email": new_emails, "Phone": phone}with open('result/employeedataoutput.csv', 'w') as f:    writer = csv.writer(f)    writer.writerow(my_dict.keys())    writer.writerows(zip(*my_dict.values()))